import matplotlib.pyplot as plt

html_mask = """<!DOCTYPE html>
<html>
<body>
<h1> {} </h1>
<img src={}>
</body>"""

def make_plot(plot_path, data):
    """generates a .png plot file of data in plot_path"""
    time_line = [data_point.time_stamp for data_point in data]
    plt.figure()
    plt.plot(time_line, [data_point.voltage for data_point in data])
    plt.plot(time_line, [data_point.currant for data_point in data])
    plt.savefig('{}/data_{}'.format(plot_path, data[0].time_stamp))


def make_html(out_file, data_path):
    """uses a mask to generate a simple html file with title and data plot"""
    out_file.write(html_mask.format('power consumption', data_path))
